--
-- Tábla szerkezet: `kerti_uzenet`
--

CREATE TABLE IF NOT EXISTS `kerti_uzenet` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `email` varchar(40) COLLATE latin2_hungarian_ci DEFAULT NULL,
  `nev` varchar(80) COLLATE latin2_hungarian_ci DEFAULT NULL,
  `message` text COLLATE latin2_hungarian_ci,
  `rogzites` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin2 COLLATE=latin2_hungarian_ci AUTO_INCREMENT=22 ;

